// src/core/multivault/create-atoms.ts
async function multiVaultCreateAtoms(config, inputs) {
  const { address, walletClient, publicClient } = config;
  const { args, value } = inputs;
  const { request } = await publicClient.simulateContract({
    account: walletClient.account,
    address,
    abi: MultiVaultAbi,
    functionName: "createAtoms",
    args,
    value
  });
  return await walletClient.writeContract(request);
}

// src/core/multivault/create-triples.ts
async function multiVaultCreateTriples(config, inputs) {
  if (inputs.args[0].length !== inputs.args[1].length || inputs.args[0].length !== inputs.args[2].length || inputs.args[0].length !== inputs.args[3].length) {
    throw new Error("All input arrays must have the same length");
  }
  const { address, walletClient, publicClient } = config;
  const { args, value } = inputs;
  const { request } = await publicClient.simulateContract({
    account: walletClient.account,
    address,
    abi: MultiVaultAbi,
    functionName: "createTriples",
    args,
    value
  });
  return await walletClient.writeContract(request);
}

// src/core/multivault/deposit.ts
async function multiVaultDeposit(config, inputs) {
  const { address, walletClient, publicClient } = config;
  const { args, value } = inputs;
  const { request } = await publicClient.simulateContract({
    account: walletClient.account,
    address,
    abi: MultiVaultAbi,
    functionName: "deposit",
    args,
    value
  });
  return await walletClient.writeContract(request);
}

// src/core/multivault/get-atom-cost.ts
async function multiVaultGetAtomCost(config) {
  const { address, publicClient } = config;
  return await publicClient.readContract({
    address,
    abi: MultiVaultAbi,
    functionName: "getAtomCost"
  });
}
